import React from "react";
import "./HeaderArea.css";
import {FaYoutube} from 'react-icons/fa';
import {MdSearch, MdVideoCall, MdViewComfy} from 'react-icons/md';
import {AiTwotoneBell} from 'react-icons/ai';
import { HiMicrophone } from "react-icons/hi";

function HeaderArea() {
    return (
        <nav className="navbar fixed-top navbar-expand-lg navbar-light bg-light">
        <button type="button" className="btn btn-default text-dark d-none d-lg-block d-md-block" id="sidebarToggle" onClick={handleSubmit}>
        <span className="navbar-toggler-icon"></span>
        </button>
        <a className="navbar-brand ml-2" href="/">
            <FaYoutube color="blue" fontSize="2rem" />
            <span>MeTube</span><sup>IN</sup>
        </a>
        
        <button className="navbar-toggler border-0 d-none" type="button" data-toggle="collapse" data-target="#navbarSupportedContent,#navbarSupportedContent2" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
      
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav ml-auto">
            <form className="form-inline my-2 my-lg-0">
                <div className="input-group">
                    <input type="text" className="form-control px-5" aria-label="Search" placeholder="Search" />
                    <div className="input-group-append">
                        <button type="button" className="input-group-text bg-white px-4">
                            <MdSearch fontSize="1.5rem" />
                        </button>
                    </div>
                </div>
                <button type="button" className="btn btn-search ml-3 border">
                    <HiMicrophone fontSize="1.3rem" />
                </button>
            </form>
          </ul>
        </div>
        <div className="d-flex justify-content-between">

                <a className="nav-link text-dark" href="/"><MdVideoCall fontSize="2rem" /></a>
            
                <a className="nav-link text-dark" href="/"><MdViewComfy fontSize="2rem" /></a>
           
                <a className="nav-link text-dark" href="/"><AiTwotoneBell fontSize="2rem" /></a>
            
                <a className="nav-link text-dark" href="/"><img src="/images/ankit.png" alt="DP" /></a>
        </div>
      </nav>
    );

    function handleSubmit(e) {
        e.preventDefault();
        document.body.classList.toggle('sb-sidenav-toggled');
        localStorage.setItem('sb|sidebar-toggle', document.body.classList.contains('sb-sidenav-toggled'));
      }
}


export default HeaderArea;
